SELECT NowDisplay.Campaign, NowDisplay.AdGroup, NowDisplay.AdDetails, Sum(NowDisplay.Cost) AS Now_Cost, Sum(PrevDisplay.Cost) AS Prev_Cost, [Now_Cost]-[Prev_Cost] AS CostChange, Sum(NowDisplay.GP) AS Now_GP, Sum(NowDisplay.GP) AS Prev_GP, [Now_GP]-[Prev_GP] AS ConvChange, [Now_GP]-[Now_Cost] AS NowDisplay_NetProfit, [Prev_GP]-[Prev_Cost] AS PrevDisplay_NetProfit, [NowDisplay_NetProfit]-[PrevDisplay_NetProfit] AS NetChange, Avg(NowDisplay.CTR) AS Now_CTR, Avg(PrevDisplay.CTR) AS Prev_CTR, [Now_CTR]-[Prev_CTR] AS CTRChange, Avg(NowDisplay.AvgCPC) AS Now_AvgCPC, Avg(PrevDisplay.AvgCPC) AS Prev_AvgCPC, [Now_AvgCPC]-[Prev_AvgCPC] AS AvgCPCChange
FROM NowDisplay INNER JOIN PrevDisplay ON (NowDisplay.AdGroupID = PrevDisplay.AdGroupID) 
GROUP BY NowDisplay.Campaign, NowDisplay.AdGroup, NowDisplay.AdDetails;
